import { Text, StyleSheet, View, Image, TextInput, Button } from "react-native";
import { useState } from "react";

const user = {
  id: "u1",
  image:
    "https://notjustdev-dummy.s3.us-east-2.amazonaws.com/avatars/vadim.jpg",
  name: "Vadim Savin",
};

const CreatPostScreen = () => {
    const descriptionState = useState("");

    const description = descriptionState[0];
    const setDescription = descriptionState[1];
    console.warn('description', description);

    const onSubmit = () => {
        console.warn('submitted');
    }
  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Image source={{ uri: user.image }} style={styles.image} />
        <Text style={styles.name}>{user.name}</Text>
      </View>
      <TextInput placeholder="What's happening?" multiline />
      <Button
        title="Post"
        onPress={onSubmit}
        style={styles.button}
        color="red"
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    width: "100%",
    padding: 20,
    paddingTop: 50,
    backgroundColor: "white",
  },
  header: {
    flexDirection: "row",
    alignItems: "center",
    width: "100%",
    marginBottom: 15,
  },
  image: {
    width: 40,
    height: 40,
    borderRadius: 30,
    marginRight: 10,
  },
  name: {
    fontWeight: "bold",
  },
});

export default CreatPostScreen;
